#include <iostream>using namespace std;// funcsvoid summ(int var){	setlocale(LC_ALL, "Russian");	int result = 0;	int var1;	int var2;	switch (var)	{	case 1:		cout << "Введите первое слагаемое: ";		cin >> var1;		cout << "Введите второе слагаемое: ";		cin >> var2;		result = var1 + var2;		break;	case 2:		int summ_num;		cout << "Введите число слагаемых: ";		cin >> summ_num;		int i, num;		for (i = 1; i <= summ_num; i++)		{			cout << "Введите слагаемое № " << i << ": ";			cin >> num;			result += num;		}		break;	}	cout << "Результат: " << result << "\n";}void subtraction(){	setlocale(LC_ALL, "Russian");	int var1;	int var2;	cout << "Введите уменьшаемое: ";	cin >> var1;	cout << "Введите вычитаемое: ";	cin >> var2;	cout << "Результат: " << var1 - var2 << "\n";}void multiplication(int var){	setlocale(LC_ALL, "Russian");	int result = 1;	int var1;	int var2;	switch (var)	{	case 1:		cout << "Введите первый сножитель: ";		cin >> var1;		cout << "Введите второй множитель: ";		cin >> var2;		result = var1 * var2;		break;	case 2:		int summ_num;		cout << "Введите число множителей: ";		cin >> summ_num;		int i, num;		for (i = 1; i <= summ_num; i++)		{			cout << "Введите множитель № " << i << ": ";			cin >> num;			result *= num;		}		break;	}	cout << "Результат: " << result << "\n";}void division(){	setlocale(LC_ALL, "Russian");	int var1;	int var2;	cout << "Введите делимое: ";	cin >> var1;	cout << "Введите делитель: ";	cin >> var2;	cout << "Результат: " << (var1 - var2) << "\n";}void exponentiation(){	setlocale(LC_ALL, "Russian");	int var1;	int var2;	cout << "Введите число, которое хотите возвести в степень: ";	cin >> var1;	cout << "Введите степень, в которую хотите возвести число: ";	cin >> var2;	cout << "Результат: " << pow(var1, var2) << "\n";}int main(){	setlocale(LC_ALL, "Russian");	bool var = true;	int znak;	while (var)	{		cout << "Выберите действие из списка: \n 1. Сложение \n 2. Вычитание \n 3. Умножение \n 4. Деление \n 5. Возведение в степень (x ** y) \n 0. Выйти из калькулятора \n : ";		cin >> znak;		switch (znak)		{		case 0:			var = false;			break;		case 1:			int var;			cout << "Выберите вариант: \n 1. Сложение двух слагаемых \n 2. Сложение нескольких слагаемых \n : ";			cin >> var;			summ(var);			break;		case 2:			subtraction();			break;		case 3:			cout << "Выберите вариант: \n 1. Умножение двух множителей \n 2. Умножение нескольких множителей \n : ";			cin >> var;			multiplication(var);			break;		case 4:			division();			break;		case 5:			exponentiation();			break;		}	}}